    <script type="text/javascript">
        var qty = false;
        document.addEventListener("DOMContentLoaded", function(event) {
            init_add_to_cart(20);
            $(document).on('change', '#quantity_wanted', function () {
                qty = $(this).val();
                //console.log('Quantity wanted: '+ qty);
            });
            function formatedNumberToFloat(price) {
                price = price.replace(prestashop.currency.sign, '');
                price = price.replace(prestashop.currency.iso_code, '');
                switch (parseInt(prestashop.currency.format)) {
                    case 1:
                        return parseFloat(price.replace(',', '').replace(' ', ''));
                    case 2:
                        return parseFloat(price.replace(' ', '').replace(',', '.'));
                    case 3:
                        return parseFloat(price.replace('.', '').replace(' ', '').replace(',', '.'));
                    case 4:
                        return parseFloat(price.replace(',', '').replace(' ', ''));
                    default:
                        return price;
                }
            }

            function init_add_to_cart(tries) {
                if (typeof jQuery === 'undefined') {
                    if (tries > 0) {
                        setTimeout(function() { init_add_to_cart(tries - 1) }, 250);
                    } else {
                        console.log('jQuery Could not be detected, AddToCart events will not be triggered');
                    }
                } else {
                    if (typeof (prestashop) === 'object') {
                        prestashop.on('updateCart', function(event) {
                            window.fb_pixel_event_id = getCookie('fb_pixel_event_id');
                            if (typeof event.reason.cart !== 'undefined') {
                                sendAddToCartFromResponse(event.reason, true, true);
                            } else if (typeof event.reason.idProduct !== 'undefined') {
                                // Add To Cart in the product list
                                $.ajax({
                                    url: 'https://smarttechnology-eg.com/en/module/facebookconversiontrackingplus/AjaxConversion',
                                    type: 'POST',
                                    cache: false,
                                    data: {
                                        id_product : event.reason.idProduct,
                                        id_product_attribute : event.reason.idProductAttribute | 0,
                                        event: 'AddToCart',
                                        rand: Math.floor((Math.random() * 100000) + 1),
                                        token: 'ec3cbc7277ebb7871f4397e5b13de7f6',
                                    }
                                })
                                .done(function(data) {
                                    if (data.return != 'error') {
                                        //console.log(data);
                                        trackAddToCart(data.custom_data, data.event_id)
                                        window.fctp_wishlist_act = true;
                                        setTimeout(function() { window.fctp_wishlist_act = false; }, 500);
                                    }
                                })
                                .fail(function(jqXHR, textStatus, errorThrown) {
                                    console.log(
                                        'Pixel Plus: Failed to track the AddToWishlist Event');
                                });
                            }
                            /*
                            if (event && event.reason) {
                                var data = {
                                    id_product_attribute: event.reason.idProductAttribute,
                                    id_product: event.reason.idProduct,
                                    action: event.reason.linkAction
                                };
                                console.log(getCookie('fb_pixel_event_id'));
                                if (requestData.action == 'add-to-cart') {

                                } else if (requestData.action == 'delete-from-cart') {
                                    // removeFromCart event
                                }
                            }*/
                        });
                    } else {
                        XMLHttpRequest.prototype.open = (function(open) {
                            return function(method, url, async) {
                                var checkURL = url.search('/cart');
                                if (checkURL > -1) {
                                    delete window.content_ids_data;
                                    delete window.content_ids_product;
                                    delete window.total_products_value;
                                    window.fb_pixel_event_id = getRandomString(12);
                                    var checkQuestion = url.search('\\?');
                                    if (checkQuestion > -1) {
                                        url = url + '&fb_pixel_event_id=' + window.fb_pixel_event_id;
                                    } else {
                                        url = url + '?fb_pixel_event_id=' + window.fb_pixel_event_id;
                                    }

                                }
                                this.addEventListener('load', function() {
                                    if (this.response != '') {
                                        try {
                                            sendAddToCartFromResponse(this.response, false, false);
                                        } catch (e) {
                                            // Is not an AddToCart event, no action needed
                                            //console.log("Can't be parsed the output to json");
                                        }
                                    }
                                });
                                open.apply(this, arguments);
                            };
                        })(XMLHttpRequest.prototype.open);
                    }
                }
            }
            function unique(array) {
                return $.grep(array, function(el, index) {
                    return index === $.inArray(el, array);
                });
            }
            function sendAddToCartFromResponse(r, parsed, delCookie) {
                if (!parsed) {
                    r = JSON.parse(r);
                }
                if (typeof r.cart == 'object') {
                    if ((typeof r.cart.products == 'object')) {
                        window.content_name = 'DELL MONITOR E1920H FLAT 19 INCH LED HD 60HZ';
                        window.content_category = 'Home > Monitors > Business Monitors';
                        //cart value should never be 0 or empty, so assigning miniumm value as 1
                        window.content_value = 1;
                        window.content_ids_data = [];
                        window.content_ids_product = [];
                        var selected_product_id = r.id_product ? r.id_product : r.idProduct;
                        var ipa = r.id_product_attribute ? r.id_product_attribute : r.idProductAttribute;
                        $.each(r.cart.products, function(key, value) {
                            var id_combination = '';
                                                        if ((selected_product_id == value.id_product && value.id_product_attribute == 0)
                                || (selected_product_id == value.id_product && value.id_product_attribute > 0 && value.id_product_attribute == ipa)) {
                                var pprice = 0;
                                if (typeof value.price_with_reduction !== 'undefined') {
                                    pprice = value.price_with_reduction;
                                } else if(typeof value.price_without_reduction !== 'undefined') {
                                    pprice = value.price_without_reduction;
                                } else {
                                    pprice = formatedNumberToFloat(value.price);
                                }
                                if (typeof value.name !== 'undefined') {
                                    content_name = value.name;
                                }
                                content_value = pprice.toFixed(pp_price_precision);
                                var pid = '' + value.id_product + id_combination;
                                var this_product = {
                                    'id': pid,
                                    'quantity': (qty !== false ? qty : value.quantity),
                                    'item_price': (qty !== false ? qty * pprice : value.quantity * pprice),
                                }
                                if (typeof value.category !== 'undefined' && value.category != '') {
                                    this_product.category = value.category;
                                }
                                content_ids_data.push(this_product);
                                content_ids_product.push(pid);
                            }
                        });

                        window.total_products_value = r.cart
                            .totals.total
                            .amount;
                        //here we suppose to sent the add to cart event
                        var cartValues = {
                            'content_name': window.content_name,
                            'content_ids': unique(window.content_ids_product),
                            'contents' : unique(window.content_ids_data),
                            'content_type': 'product',
                            'value': content_value,
                            'currency': 'EGP'
                        };

                                                if (window.content_category != '') {
                            cartValues['content_category'] = window
                                .content_category;
                        }
                        if (cartValues.content_type != '' && cartValues.contents != '' &&
                            cartValues.content_ids != '' && cartValues.value != '' &&
                            cartValues.currency != '') {
                            trackAddToCart(cartValues, getCookie('fb_pixel_event_id'));
                        } else {
                            console.log("fbq error: Invalid values in the contents or the cart item is deleted");
                        }
                        if (delCookie) {
                            deleteCookie('fb_pixel_event_id');
                        }
                    }
                }
            }
            function trackAddToCart(values, event_id) {
                fbq('track', 'AddToCart', values, {eventID: event_id});
            }
        });
